const body=document.querySelector("body"),navbar=document.querySelector(".navbar-menu"),menu=document.querySelector(".menu-list"),menuBtn=document.querySelector(".menu-btn"),cancelBtn=document.querySelector(".cancel-btn");menuBtn.onclick=()=>{menu.classList.add("active"),menuBtn.classList.add("hide"),cancelBtn.classList.add("show"),body.classList.add("disabledScroll")},cancelBtn.onclick=()=>{menu.classList.remove("active"),menuBtn.classList.remove("hide"),cancelBtn.classList.remove("show"),body.classList.remove("disabledScroll")},window.onscroll=()=>{20<this.scrollY?navbar.classList.add("sticky"):navbar.classList.remove("sticky")};var skills={ht:92,wp:75,mg:86,ux:88};$.each(skills,function(e,s){$("."+e).animate({width:s+"%"},3e3,function(){$(".skill__bar__inside").fadeIn()})});